import React, { useState } from "react";
import axios from "axios";

function OneWordForm() {
  const [word, setWord] = useState("");
  const [explanation, setExplanation] = useState("");
  const [result, setResult] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://127.0.0.1:5000/analyze", {
        word,
        explanation,
      });
      setResult(response.data);
    } catch (error) {
      console.error("Error analyzing:", error);
    }
  };

  return (
    <div className="form-container">
      <form onSubmit={handleSubmit}>
        <label>Your One Word:</label>
        <input
          type="text"
          value={word}
          onChange={(e) => setWord(e.target.value)}
          placeholder='e.g. "Determined", "Curious"'
          required
        />

        <label>Explain why:</label>
        <textarea
          value={explanation}
          onChange={(e) => setExplanation(e.target.value)}
          placeholder="Write a short explanation..."
          required
        ></textarea>

        <button type="submit">Analyze</button>
      </form>

      {result && (
        <div className="result">
          <h3>üîç Analysis Result</h3>
          <p>
            <strong>Score:</strong> {result.rating} / 10
          </p>
          <p>
            <strong>Feedback:</strong> {result.feedback}
          </p>
        </div>
      )}
    </div>
  );
}
{
  result && (
    <div className="result">
      <h3>üîç Analysis Result</h3>
      <p>
        <strong>Overall Score:</strong> {result.rating} / 10
      </p>
      <p>
        <strong>Word Score:</strong> {result.word_sentiment} / 10
      </p>
      <p>
        <strong>Explanation Score:</strong> {result.explanation_sentiment} / 10
      </p>
      <p>
        <strong>Feedback:</strong> {result.feedback}
      </p>
    </div>
  );
}

export default OneWordForm;
